package com.kent.learningdemo.main;

import android.app.Activity;
import android.content.Intent;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.util.Log;
import android.widget.ListView;

import com.kent.learningdemo.R;
import com.kent.learningdemo.constant.Constants;
import com.kent.learningdemo.main.adapter.HomeAdapter;
import com.kent.learningdemo.receiver.RelativeStartupReceiver;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.OutputStream;

public class MainActivity extends Activity {

    private ListView mListView;
    private HomeAdapter mAdapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        init();
        Intent intent = new Intent();
        intent.setAction("com.kent.autostartup");
        sendBroadcast(intent);

        String content = "";

        byte[] bytes = Base64.decode(content, Base64.DEFAULT);

        try {
            File file = new File(getCacheDir() + File.separator + "a.png");
            OutputStream stream = new FileOutputStream(file.getPath());
            Bitmap bitmap = BitmapFactory.decodeByteArray(bytes, 0, bytes.length);
            bitmap.compress(Bitmap.CompressFormat.JPEG, 100, stream);
            stream.close();
            if (bitmap!=null) {
                bitmap.recycle();
                bitmap = null;
            }
        } catch (IOException e) {
            Log.e("test","saveBitmap",e);
        }
    }

    private void init(){
        this.mListView = (ListView)findViewById(R.id.list_view);

        mAdapter = new HomeAdapter(this, Constants.mDemoList);

        this.mListView.setAdapter(mAdapter);
    }
}
